import { useState } from 'react';
import './App.css';


function App() {
  let myName = "Chathu"; 
  // hook
  const [myName2, setMyName2] =  useState("Wije");

  function handleCLick() {
    alert(myName);
    myName = "Silva";  
    alert(myName);
    myName2 = "perera"; 
  }

  return (
    <div className="App">
      <h1>Hello</h1>
      <button 
        onClick={handleCLick}
      >
        Click Me
      </button>
      <br />
      {myName}
      <br />
      { myName2 }
    </div>
  );
}

export default App;
